mapper.select:
(SELECT '1' id, price ,tupr.ctime,order_id,twc.type AS name FROM tb_user_purchase_record tupr JOIN tb_water_card twc ON tupr.card_no=twc.code WHERE card_no=#{cardNo}
AND DATE_FORMAT(tupr.ctime, '%Y-%m-%d')=DATE_FORMAT(#{date}, '%Y-%m-%d')
AND DATE_FORMAT(tupr.ctime, '%Y-%m')=DATE_FORMAT(#{date}, '%Y-%m')
) UNION (SELECT '2' id,price,turr.ctime,order_id,twc2.type AS name FROM tb_user_recharge_record turr JOIN tb_water_card twc2 ON turr.card_id=twc2.code WHERE card_id=#{cardNo}
AND DATE_FORMAT(turr.ctime, '%Y-%m-%d')=DATE_FORMAT(#{date}, '%Y-%m-%d')
AND DATE_FORMAT(turr.ctime, '%Y-%m')=DATE_FORMAT(#{date}, '%Y-%m')
) ORDER BY ctime DESC

SELECT sum(price) AS balance
FROM tb_user_purchase_record WHERE DATE_FORMAT(ctime, '%Y-%m')=DATE_FORMAT(now(), '%Y-%m')

    AND card_no=#{cardNo}

SELECT * FROM tb_user_purchase_record WHERE 1=1
AND user_id=#{userId}
ORDER BY ctime LIMIT 0,1

SELECT tupr.id,tupr.user_id,price,water_consumption,unit_cost,twc.balance balance
,tupr.place,tupr.name,tupr.card_no,tupr.ctime,tupr.collector_id,tupr.order_id,card_no,
(SELECT sum(price) FROM tb_user_purchase_record WHERE user_id=tupr.user_id) AS total_price,
(SELECT sum(water_consumption) FROM tb_user_purchase_record WHERE user_id=tupr.user_id)AS total_consumption
FROM tb_user_purchase_record tupr JOIN tb_water_card twc ON tupr.card_no=twc.code WHERE 1=1

    AND tupr.user_id=#{userId}


    AND tupr.name LIKE #{value}


    AND tupr.ctime > #{date1}


    AND DATE_FORMAT(#{date2}, '%Y-%m-%d')>=DATE_FORMAT(tupr.ctime, '%Y-%m-%d')

SELECT sum(water_consumption) AS water_consumption, sum(unit_cost*tb_user_purchase_record.water_consumption) AS unit_cost FROM tb_user_purchase_record WHERE 1=1
and DATE_FORMAT(ctime, '%Y-%m-%d')=DATE_FORMAT(#{date}, '%Y-%m-%d')
AND user_id=#{id}

SELECT DATE_FORMAT(ctime,'%H') name, count(*) total_price, sum(price) price, sum(water_consumption) AS water_consumption FROM tb_user_purchase_record WHERE DATE_FORMAT(ctime, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d')
AND user_id=#{id}
GROUP BY hour(ctime)

SELECT day(ctime) name, sum(price) price, sum(water_consumption) AS water_consumption FROM tb_user_purchase_record WHERE DATE_FORMAT(ctime, '%Y-%m') = DATE_FORMAT(#{date}, '%Y-%m')
AND user_id=#{id}
GROUP BY day(ctime)
