mapper.select:
SELECT turr.id,turr.user_id,price,twc.balance balance,order_id,turr.ctime,name,
 (SELECT sum(price) FROM tb_user_recharge_record WHERE user_id=turr.user_id) AS total_price
 FROM tb_user_recharge_record turr JOIN tb_water_card twc ON turr.card_id=twc.code
 WHERE 1=1

     AND turr.user_id=#{id}


     AND turr.name LIKE #{value}


     AND turr.ctime > #{date1}


     AND DATE_FORMAT(#{date2}, '%Y-%m-%d')>=DATE_FORMAT(turr.ctime, '%Y-%m-%d')

 ORDER BY ctime DESC

SELECT DATE_FORMAT(ctime,'%H') name, sum(price) price FROM tb_user_recharge_record WHERE DATE_FORMAT(ctime, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d')
AND user_id=#{id}
GROUP BY hour(ctime)

SELECT day(ctime) name, sum(price) price FROM tb_user_recharge_record WHERE DATE_FORMAT(ctime, '%Y-%m') = DATE_FORMAT(#{date}, '%Y-%m')
AND user_id=#{id}
GROUP BY day(ctime)
