mapper.select:
SELECT * FROM tb_user_recharge_record WHERE 1=1

    AND user_id=#{id}


    AND tb_user_recharge_record.name LIKE #{value}


    AND ctime > #{date1}


    AND DATE_FORMAT(#{date2}, '%Y-%m-%d')>=DATE_FORMAT(ctime, '%Y-%m-%d')

ORDER BY ctime DESC

SELECT DATE_FORMAT(ctime,'%H') name, sum(price) price FROM tb_user_recharge_record WHERE DATE_FORMAT(ctime, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d')
AND user_id=#{id}
GROUP BY hour(ctime)

SELECT day(ctime) name, sum(price) price FROM tb_user_recharge_record WHERE DATE_FORMAT(ctime, '%Y-%m') = DATE_FORMAT(#{date}, '%Y-%m')
AND user_id=#{id}
GROUP BY day(ctime)
